mapper.insert:
insert into notice(
	   notice_no
	 , title
	 , content
	 , write_date
	 , nwriter
	 )values(
	   #{notice_no}
	 , #{title}
	 , #{content}
	 , sysdate
	 , #{nwriter}
	 )

mapper.update:
update notice set
	   title = #{title}
     , content = #{content}
 where notice_no = #{notice_no}

	update notice_file_list 
	   set nno = #{nno}
		 , writer = #{writer}
		 , filename = #{fileName} 
     where notice_no = #{notice_no}
